import React, { useState } from "react";
import "./Cursor.css";

// window.onmousemove = (e) => {
//   cur1.setAttribute("style", "top:" + e.pageY + "px; left:" + e.pageX + "px;");
//   cur2.setAttribute("style", "top:" + e.pageY + "px; left:" + e.pageX + "px;");
// };

const Cursor = () => {
  const [c1, setc1] = useState(true);
  window.addEventListener("mousemove", (e) => {
    setc1("top:" + e.pageY + "px; left:" + e.pageX + "px;");
    var c2 = "top:" + e.pageY + "px; left:" + e.pageX + "px;";
  });

  return (
    <>
      <div className="cursor-1" style={{ c1 }}></div>
      <div className="cursor-2" style={window.c2}></div>
    </>
  );
};

export default Cursor;
