import React from "react";
import "./Cursor.css";

let cur1 = document.querySelector(".cursor-1");
let cur2 = document.querySelector(".cursor-2");

// window.onmousemove = (e) => {
//   cur1.setAttribute("style", "top:" + e.pageY + "px; left:" + e.pageX + "px;");
//   cur2.setAttribute("style", "top:" + e.pageY + "px; left:" + e.pageX + "px;");
// };
document.addEventListener("mousemove", (e) => {
  const cursor = document.getElementsByClassName("cr1")[0];
  cursor.setAttribute(
    "style",
    "top:" + e.pageY + "px; left:" + e.pageX + "px;"
  );
});

const Cursor = () => {
  return (
    <>
      <div className="cursor-1 cr1"></div>
      <div className="cursor-2 cr2"></div>
    </>
  );
};

export default Cursor;
