const Cursor = () => {
  return (
    <>
      {window.addEventListener("mousemove", (e) => {
        let c1 = "top:" + e.pageY + "px; left:" + e.pageX + "px;";
        <div className="cursor-1" style={c1}></div>;
        console.log(c1);
        let c2 = "top:" + e.pageY + "px; left:" + e.pageX + "px;";
      })}

      <div className="cursor-1"></div>
      <div className="cursor-2"></div>
    </>
  );
};

export default Cursor;
