const Cursor = () => {
  const [c1t, setc1t] = useState(true);
  const [cll, setc1l] = useState(true);
  window.addEventListener("mousemove", (e) => {
    setc1t("top:" + e.pageY + "px");
    setc1l("left:" + e.pageX + "px;");
  });

  return (
    <>
      {console.log(c1t)}
