const Cursor = () => {
  const [c1t, setc1t] = useState(true);
  const [c1l, setc1l] = useState(true);
  window.addEventListener("mousemove", (e) => {
    setc1t(e.pageY);
    setc1l("top:" + e.pageY + "px;");
  });

  console.log(c1t);

  return (
    <>
      <div className="cursor-1" style={{ top: { c1t } }}></div>
      <div className="cursor-2"></div>
    </>
  );
};

export default Cursor;
