import React from "react";
import "./Cursor.css";

let cur1 = document.querySelector(".cursor-1");
let cur2 = document.querySelector(".cursor-2");

// window.onmousemove = (e) => {
//   cur1.setAttribute("style", "top:" + e.pageY + "px; left:" + e.pageX + "px;");
//   cur2.setAttribute("style", "top:" + e.pageY + "px; left:" + e.pageX + "px;");
// };

const Cursor = () => {
  return (
    <>
      {window.addEventListener("mousemove", (e) => {
        window.c1 = "top:" + e.pageY + "px; left:" + e.pageX + "px;";
        window.c2 = "top:" + e.pageY + "px; left:" + e.pageX + "px;";
      })}

      <div className="cursor-1" style={window.c1}></div>
      <div className="cursor-2" style={window.c2}></div>
    </>
  );
};

export default Cursor;
