import React, { useState } from "react";
import "./Cursor.css";

document.addEventListener("mousemove", (e) => {
  const cursor = document.getElementsByClassName("cr1")[0];
  cursor.setAttribute(
    "style",
    "top:" + e.pageY + "px; left:" + e.pageX + "px;"
  );
});
document.addEventListener("mousemove", (e) => {
  const cursor = document.getElementsByClassName("cr2")[0];
  cursor.setAttribute(
    "style",
    "top:" + e.pageY + "px; left:" + e.pageX + "px;"
  );
});

const Cursor = () => {
  const [linkMouseHover, setLinkMouseHover] = useState(true);

  const mHover = () => {
    if (window.onmouseover) {
      setLinkMouseHover(true);
    } else {
      setLinkMouseHover(false);
    }
  };

  const linkHoverA = document.querySelectorAll(a);

  linkHoverA.addEventListener("mouseover", mHover);
