    <>
      <div
        className={linkMouseHover ? "cursor-1 cr1 active" : "cursor-1 cr1"}
      ></div>
      <div
        className={linkMouseHover ? "cursor-2 cr2 active" : "cursor-2 cr2"}
      ></div>
    </>
  );
};

export default Cursor;
