import React, { useState } from "react";
import "./Header.css";
import NavItem from "./Navitem";
import NavitemApi from "../Api/NavItemApi";
import Follow from "./Follow";
import FollowLink from "../Api/FollowApi";

const Header = () => {
  const [navItem] = useState(NavitemApi);
  const [followLink] = useState(FollowLink);
  const [active, setActive] = useState(true);
  const [navbar, setNavbar] = useState(false);
  const FName = "siam";
  const LName = "chowdhury";

  const hideNav = () => {
    console.log(window.scroll);
    if (window.scrollY) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  window.addEventListener("scroll", hideNav);

  return (
    <>
      <div
        id="menuBar"
        onClick={() => setActive(!active)}
        className={active ? "fas fa-bars" : "fas fa-times"}
      ></div>
      <header className={navbar ? `${active ? "" : "active"}` : ""}>
        <div className="img">
          <img src="images/siam1.jpg" alt="" />
        </div>
        <a href="#" className="logo">
          <span>{FName} </span>
          {LName}
        </a>

        <NavItem navItem={navItem} />
        <Follow followLink={followLink} />
      </header>
    </>
  );
};

export default Header;
